                $('#c_email').text(value.email);

                var startDateTime = value.forward_date; 
                var currentDateTime = new Date();
                var newDateTime = moment(startDateTime).format("MMMM D YYYY, h:mm:ss a");
                $('#forward_date').text(newDateTime);
        
                var date1 = new Date(startDateTime);

                // To calculate the no. of days between two dates
