var ToggleForm = function(bool) {
    if ( bool ) {
        AssementID = "";
        GenID      = "";
        BussID     = "";
        $('#business_name').text("--");
        $('#business_id').text("--");
        $('#subtotal-inspection').text(parseFloat(0).toFixed(2));
        $('#subtotal-water-stations').text(parseFloat(0).toFixed(2));
        $('#subtotal-water-analysis').text(parseFloat(0).toFixed(2));
        $('#totalSanitaryAmount').text(parseFloat(0).toFixed(2));
    }
    $("#form_sanitary_assess")[0].reset();
    // $("#form_update_sanitary")[0].reset();
    $('#form_sanitary_assess :input').attr('disabled', bool);
    $('#form_sanitary_assess [name="selAreaSize"]').attr('disabled', bool);
    $('#form_sanitary_assess input[type=radio]').removeAttr('checked');
    $("#btnProceed").attr('disabled', bool);



    $("#btnCancelAssess").attr('disabled', bool);
}

var DoCompute = function(){
    // Compute Total Amount of Sanitary
    var potability_amount  = parseInt($("input[name='potability_amount']").val());
    var drinking_water     = parseInt($("input[name='dws_amount']").val());

    var totalInspection    = parseInt($('#totalAreaAmount').val());
    var totalWaterStations = parseFloat(potability_amount + drinking_water);
    var totalWaterAnalysis = parseInt($('#totalBottleAmount').val());
    var totalSanitary      = parseFloat(totalInspection + totalWaterStations + totalWaterAnalysis);

    var bool = (totalSanitary > 0) ? false : true;
    $("#btn_proceed").attr('disabled', bool);
    
    total = totalSanitary;
    $("#subtotal-inspection").text(parseFloat(totalInspection).toFixed(2));
    $("#subtotal-water-stations").text(parseFloat(totalWaterStations).toFixed(2));
    $('#subtotal-water-analysis').text(parseFloat(totalWaterAnalysis).toFixed(2));
    $("#totalSanitaryAmount").text(parseFloat(totalSanitary).toFixed(2));
}

var UpdateCompute = function(){

    //Update Computation if Perform Changes
    var update_potability_amount = parseInt($("input[name='update_chkpotability_amount']").val());
    var update_siteclearance_amount = parseInt($("input[name='update_chksiteclearance_amount']").val());
    var update_inspection_amount = parseInt($("input[name='update_totalAreaAmount']").val());
    var update_totalWaterStations = parseFloat(update_potability_amount + update_siteclearance_amount);
    var update_totalWaterAnalysis = parseInt($("input[name='update_wateranalysis_amount']").val())
    var update_totalSanitary  = parseFloat(update_inspection_amount + update_totalWaterStations + update_totalWaterAnalysis);

    var bool = (update_totalSanitary > 0) ? false : true;
    $("#btn_save_changes").attr('disabled', bool);
    
    total = update_totalSanitary;
    $("#update_subtotal_inspection").text(parseFloat(update_inspection_amount).toFixed(2));
    $("#update_subtotal_waterstations").text(parseFloat(update_totalWaterStations).toFixed(2));
    $('#update_subtotal_wateranalysis').text(parseFloat(update_totalWaterAnalysis).toFixed(2));
    $("#update_totalSanitary").text(parseFloat(update_totalSanitary).toFixed(2));
}

