    $('input[type=number]').on('click change keyup paste', function(){
        DoCompute();
        UpdateCompute();
    })

    $("select[name='selAreaSize'] , select[name='update_sel_flrarea']").on('change', function(){
        var amount = ($(this).val() != "") ? $(this).find('option:selected').attr('data-amount') : 0;
        $("#subtotal-inspection, #update_subtotal_inspection").text(parseFloat(amount).toFixed(2));
        $("input[name='totalAreaAmount'], input[name='update_totalAreaAmount']").val(parseFloat(amount).toFixed(2));
        DoCompute();
        UpdateCompute();
    })

    $("input[name='chk_potability'] , input[name='update_chkpotability']").on('change', function(){
        var amount = ($(this).is(":checked")) ? $(this).attr('data-amount') : 0;
        $("input[name='potability_amount'] , input[name='update_chkpotability_amount']").val(parseFloat(amount).toFixed(2));
        DoCompute();
        UpdateCompute();

    })

    $("input[name='chk_dws'] , input[name='update_chksiteclearance']").on('change', function(){
        var amount = ($(this).is(":checked")) ? $(this).attr('data-amount') : 0;
        $("input[name='dws_amount'], input[name='update_chksiteclearance_amount']").val(parseFloat(amount).toFixed(2));
        DoCompute();
        UpdateCompute();

    })

    $("input[name='rbd_pbw'], input[name='update_rdbwateranalaysis']").on('change', function(){
        if ( $(this).val() == 1 ) {
            $("#bottleCount, input[name='update_total_bottles']").val('0').attr('readonly', true);
        } else {
            $("#bottleCount, input[name='update_total_bottles']").val('1').attr('readonly', false).focus();
        }
        var amount      = parseInt($(this).attr('data-amount'));
        var bottleCount = parseInt($("#bottleCount, input[name='update_total_bottles']").val());
        var total       = parseFloat(bottleCount * amount);
        $("#totalBottleAmount, input[name='update_wateranalysis_amount']").val(parseFloat(total).toFixed(2));
        DoCompute();
        UpdateCompute();

    })

    $("input[name='bottleCount'], input[name='update_total_bottles']").on('keyup change', function(e){
        var bottleCount = 1;
        if($(this).val() <= 0) {
            bottleCount = 1;
        } else {
            bottleCount = $(this).val();
        }
        var amount      = parseInt($("input[name='rbd_pbw']:checked, input[name='update_rdbwateranalaysis']:checked").attr('data-amount'));
        var total       = parseFloat(bottleCount * amount);
        $("#totalBottleAmount, input[name='update_wateranalysis_amount']").val(parseFloat(total).toFixed(2));
        DoCompute();
        UpdateCompute();

    })

    $("input[name='ifComplied']").on('change', function(){
        var ischecked = $('input[name="ifComplied"]:checked').val();
        if(ischecked){
            $('input[name=ifComplied]').val(1);
        }else{
            $('input[name=ifComplied]').val(0)
        }
    })


      //Get all Nature of Business by selected Risk Type
    $('[name=rdb_risk_type]').on('change', function(){
        var id = $('input[name="rdb_risk_type"]:checked').val();
        $.ajax({
            url: business_nature_url,
            type: "post",
            data: {"nature_id": id},
            success:function(res) { 
                var select = $('select[name=selNatureOfBusiness]');
                var option = "<option value=''>Select Nature of Business</>";
                $.each(res, function(index, value){
                    option += `<option value="`+value.ID+`" data-amount="`+value.amount+`">`+value.description+`</option>`;
                });
                select.empty();
                select.append(option);
            },
            error:function(a,b,c)
            {
                forSwal("An error occured. Please contact the admin.", "error", "btn-danger");
            }
        })
    })




